import React, { useState, useEffect } from "react";
import "../styles/navbar-styles.css";
import elanlogo from "../assets/elan-logo.png";
import { FaBars, FaTimes } from "react-icons/fa";
import { Link } from "react-router-dom";

function Navbar() {
  const [clicked, setClicked] = useState(false);
  const [activeNavItem, setActiveNavItem] = useState("/"); // Initialize with 'home' or the default active item.
  const [chalu, setChalu] = useState(false);
  // const navigate = useNavigate();
  const location = useLocation();

  const changeState = () => {
    setClicked(!clicked);
  };

  const changeBackground = () => {
    if (window.scrollY >= 70) {
      setChalu(true);
    } else {
      setChalu(false);
    }
  };
  useEffect(() => {
    setActiveNavItem(location.pathname);
    // Update the active link in your navigation bar based on the 'location' object.
    // You can use 'location.pathname' to determine the current route.
    // setActiveNavItem()
  }, [location]);

  const handleNavItemClick = (item, path) => {
    setActiveNavItem(item);
    setClicked(false);
    // navigate(path); // Use history to navigate to the route.
  };

  useEffect(() => {
    window.addEventListener("scroll", changeBackground);

    // Remove the event listener when the component unmounts
    return () => {
      window.removeEventListener("scroll", changeBackground);
    };
  }, []);

  return (
    <>
      <nav id={activeNavItem === "/" && !chalu && !clicked ? "navigation" : ""}>
        <Link
          to="/"
          className={activeNavItem === "/" ? "active" : ""}
          onClick={() => handleNavItemClick("/", "/")}
        >
          <div className="mobile">
            <img
              src={elanlogo}
              height={72}
              className="iithLogo"
              alt="IIT Hyderabad Logo"
            />
          </div>

          <div className="desktop">
            <img
              src={activeNavItem === "/" && chalu ? elanlogo : elanlogo}
              height={48}
              className="iithLogo"
              alt="IIT Hyderabad Logo"
            />
          </div>
        </Link>
        <div>
          <ul
            id={activeNavItem === "/" && !chalu ? "notScrolled" : ""}
            className={clicked ? "navBar clicked" : "navBar"}
          >
            <li>
              <Link
                to="/"
                className={activeNavItem === "/" ? "active" : ""}
                onClick={() => handleNavItemClick("/", "/")}
              >
                Home
              </Link>
            </li>
            <li>
              <Link to="/">Team</Link>
            </li>
            <li>
              <Link to="/">Gallery</Link>
            </li>
            <li>
              <Link to="/">About</Link>
            </li>
            <li>
              <Link to="/">Contact Us</Link>
            </li>
          </ul>
        </div>

        <div
          className={
            activeNavItem === "/" && !chalu ? "phone iconWhite" : "phone"
          }
          onClick={changeState}
        >
          <i id="bar">
            {clicked ? (
              <FaTimes />
            ) : (
              <FaBars
                color={
                  activeNavItem === "/" && !chalu
                    ? "rgb(198, 198, 198)"
                    : "black"
                }
              />
            )}
          </i>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
